SELECT id, user_id, type_id, job_id, message, created_at FROM LOG WHERE user_id = #{userId}
AND (LOWER(message) LIKE '%' || LOWER(#{search}) || '%')
ORDER BY created_at DESC OFFSET #{offset} ROWS FETCH NEXT #{limit} ROWS ONLY

SELECT id, user_id, type_id, job_id, message, created_at FROM LOG WHERE job_id = #{jobId}
AND id > #{fromId}
ORDER BY created_at ASC

SELECT COUNT(*) FROM LOG
WHERE user_id = #{userId}

    AND (LOWER(message) LIKE '%' || LOWER(#{search}) || '%')
